INSERT INTO `wp_posts` VALUES (179,1,'2014-12-17 14:06:45','2014-12-17 19:06:45','<div><p>Creating Nice Notifications with jQuery</p><blockquote class=\"zocle_blockquote\" style=\"clear:both;\"><div class=\"zocle-editor-content\"><div class=\"sort-element-content\"><div class=\"Zocle_Content_Wrapper Zocle_RSS\"><div class=\"Zocle_Content_Image_Left\"><a title=\"Creating Nice Notifications with jQuery\" href=\"http://zocle.us/gC4w\" target=\"_blank\">		    							<img src=\"http://dab1nmslvvntp.cloudfront.net/wp-content/uploads/2014/12/1417752395penguin_sitepoint.jpg\" class=\"rssShowImgSrc fr-fin\">		    						</a></div><div class=\"Zocle_Content_Details\"><h2 class=\"Zocle_Content_Headline\"><a href=\"http://zocle.us/gC4w\" target=\"_blank\">Creating Nice Notifications with jQuery</a></h2><div class=\"Zocle_Content_Teaser\">This article discusses noty, a jQuery plugin used to create different types of notifications.</div><div class=\"Zocle_Content_Date\">12/05/2014 10:50 AM</div></div></div></div></div></blockquote><p><br></p></div>','Creating Nice Notifications with jQuery','','inherit','open','open','','127-revision-v1','','','2014-12-17 14:06:45','2014-12-17 19:06:45','',127,'http://zhexiao.zz.mu/127-revision-v1/',0,'revision','',0),(234,1,'2015-01-02 14:39:08','2015-01-02 19:39:08','<div><p><br></p><blockquote class=\"zocle_blockquote\" style=\"clear:both;\"><div class=\"zocle-editor-content\" data-og-id=\"1420227396861\" data-uniqid=\"1420227396861\" contenteditable=\"false\"><div class=\"sort-element-content\"><div class=\"Zocle_Content_Wrapper Zocle_RSS\"><div class=\"Zocle_Content_Image_Left\"><a title=\"How do you connect to multiple MySQL databases on a single webpage?\" alt=\"How do you connect to multiple MySQL databases on a single webpage?\" href=\"http://zocle.us/lL4w\" target=\"_blank\">			    						<img src=\"http://cdn.sstatic.net/stackoverflow/img/apple-touch-icon@2.png?v=fde65a5a78c6\" class=\"rssShowImgSrc fr-fin\" data-num=\"1\">			    					</